//bitwise Operator demonstration
package org.tnsif.operators;

public class BitwiseOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int x=8,y=3;
          System.out.println(x&y);
          System.out.println(x|y);
          System.out.println(x^y);
          System.out.println(x>>y);
          System.out.println(x<<y);
	}

}
